import React, { useRef, useEffect, useState } from 'react';
import styles from './InputContainer.module.css';

const InputContainer = props => {
  const containerRef = useRef(null);
  const [activeName, setActiveName] = useState('');
  let propClassName = '';

  // Toggle focus/active mode
  useEffect(() => {
    if (containerRef.current && containerRef.current.querySelector('input')) {
      const input = containerRef.current.querySelector('input');

      input.addEventListener('focus', () => {
        setActiveName(styles.ActiveName);
      });

      input.addEventListener('blur', () => {
        if (input.value.length === 0) {
          setActiveName('');
        }
      });
    }
  }, []);

  // Focus input even if clicked on activeName
  const focusInput = () => {
    console.log("a");
    if (containerRef.current) {
      containerRef.current.focus();
    }
  };

  if (props.styleName) {
    propClassName = props.styleName;
  }

  return (
    <div
      className={styles.InputContainer + ' ' + propClassName}
      ref={containerRef}
    >
      <div className={styles.Name + ' ' + activeName} onClick={focusInput}>
        {props.name}
      </div>
      {props.children}
    </div>
  );
};

export default InputContainer;
