import React from 'react';
import styles from './Person.module.css';

import DoctorImg from '../../assets/images/doctor.jpg';
import NurseImg from '../../assets/images/nurse.jpg';

const Person = props => {
  let avatarSrc;
  if (props.type === 'doctor') {
    avatarSrc = DoctorImg;
  } else if (props.type === 'nurse') {
    avatarSrc = NurseImg;
  }

  return (
    <div className={styles.Person}>
      <img className={styles.Avatar} src={avatarSrc} alt="" />
      <div className={styles.Name}>{props.data.name}</div>
      <div className={styles.Country}>{props.data.country}</div>
      <div className={styles.Company}>{props.data.company}</div>
    </div>
  );
};

export default Person;
